import numpy as np
import matplotlib.pyplot as plt
from scipy import stats

def plot_dual_histograms(data1, data2):
    """
    Plot two histograms with density curves in subfigures.
    
    Args:
        data1 (list): First list of values between 0 and 1
        data2 (list): Second list of values between 0 and 1
    """
    
    # Create figure and subplots
    fig, (ax1, ax2) = plt.subplots(1, 2, figsize=(9, 5))
    bins = np.arange(0, 1.05, 0.05)
    fontfamily='serif'
    pad_title = 0
    fontsize_title = 12
    fontsize_label = 10
    line_alpha = 0.3
    grid_color = '#E0E0E0'  # Light gray color for grid
    
    # Style settings for both axes
    for ax in [ax1, ax2]:
        # Remove ticks but keep frame
        ax.tick_params(length=0)
        # Make frame dark
        for spine in ax.spines.values():
            spine.set_color('black')
            spine.set_linewidth(1.5)
        # Add horizontal grid only
        ax.yaxis.grid(True, color=grid_color, linestyle='-', linewidth=0.8, alpha=0.5)
        ax.set_axisbelow(True)  # Put grid below the plot elements
    
    # Plot first histogram with counts
    counts1, bins1, _ = ax1.hist(data1, bins=bins, density=False, alpha=0.7, color='skyblue', edgecolor='black')
    # Add count labels on top of each bar
    for i in range(len(counts1)):
        if counts1[i] > 0:  # Only show label if there are samples in the bin
            ax1.text(bins1[i] + 0.025, counts1[i], int(counts1[i]), 
                    ha='center', va='bottom', fontsize=8, fontfamily=fontfamily)
    
    # Calculate and plot density curve (scaled to match counts)
    kde1 = stats.gaussian_kde(data1)
    x1 = np.linspace(0, 1, 200)
    scaling_factor1 = len(data1) * 0.05  # Scale factor = N * bin_width
    ax1.plot(x1, kde1(x1) * scaling_factor1, color='navy', linewidth=2, alpha=line_alpha)
    
    ax1.set_title('Qwen2.5-Math-1.5B', fontsize=fontsize_title, fontfamily=fontfamily, pad=pad_title, weight='bold')
    ax1.set_xlabel('Pass@1 Rate', fontsize=fontsize_label, fontfamily=fontfamily, weight='bold')
    ax1.set_ylabel('Number of prompts', fontsize=fontsize_label, fontfamily=fontfamily, weight='bold')
    
    # Plot second histogram with counts
    counts2, bins2, _ = ax2.hist(data2, bins=bins, density=False, alpha=0.7, color='lightgreen', edgecolor='black')
    # Add count labels on top of each bar
    for i in range(len(counts2)):
        if counts2[i] > 0:  # Only show label if there are samples in the bin
            ax2.text(bins2[i] + 0.025, counts2[i], int(counts2[i]), 
                    ha='center', va='bottom', fontsize=8, fontfamily=fontfamily)
    
    # Calculate and plot density curve (scaled to match counts)
    kde2 = stats.gaussian_kde(data2)
    x2 = np.linspace(0, 1, 200)
    scaling_factor2 = len(data2) * 0.05  # Scale factor = N * bin_width
    ax2.plot(x2, kde2(x2) * scaling_factor2, color='darkgreen', linewidth=2, alpha=line_alpha)
    
    ax2.set_title('Qwen2.5-Math-7B', fontsize=fontsize_title, fontfamily=fontfamily, pad=pad_title, weight='bold')
    ax2.set_xlabel('Pass@1 Rate', fontsize=fontsize_label, fontfamily=fontfamily, weight='bold')
    
    # Adjust layout
    plt.tight_layout()
    
    return fig

